import{_ as e,a as t,o as s,ah as o}from"./chunks/framework.Ddlh_Zg3.js";const h=JSON.parse('{"title":"Relational Database Service (RDS)","description":"","frontmatter":{},"headers":[],"relativePath":"docs/cloud/aws/services/rds.md","filePath":"docs/cloud/aws/services/rds.md"}'),r={name:"docs/cloud/aws/services/rds.md"};function i(n,a,l,d,c,g){return s(),t("div",null,a[0]||(a[0]=[o('<h1 id="relational-database-service-rds" tabindex="-1">Relational Database Service (RDS) <a class="header-anchor" href="#relational-database-service-rds" aria-label="Permalink to “Relational Database Service (RDS)”">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to “Overview”">​</a></h2><p>It&#39;s a managed service that makes it easy to set up, operate, and scale a relational database in the cloud. It handles routine database tasks such as provisioning, patching, backup, recovery, and scaling.</p><details class="details custom-block"><summary>Supported Databases</summary><ul><li><a href="/se-mindmap/docs/cloud/aws/services/aurora.html">Aurora</a></li><li><strong>Postgres</strong></li><li><strong>MySQL</strong></li><li><strong>MariaDB</strong></li><li><strong>Oracle</strong></li><li><strong>Microsoft SQL Server</strong></li><li><strong>IBM DB2</strong></li></ul></details><h2 id="storage-auto-scaling" tabindex="-1">Storage Auto Scaling <a class="header-anchor" href="#storage-auto-scaling" aria-label="Permalink to “Storage Auto Scaling”">​</a></h2><p>Helps you increase storage on your RDS DB instance dynamically, scaling verticaly automatically.</p><p><strong>Maximum Storage Threshold</strong> can be set as a limit for DB storage</p><h2 id="read-replicas" tabindex="-1">Read Replicas <a class="header-anchor" href="#read-replicas" aria-label="Permalink to “Read Replicas”">​</a></h2><p><strong>Read Replicas</strong> allow you to scale out read-intensive workloads by creating one or more read-only copies of your database.</p><details class="details custom-block"><summary>Details</summary><ul><li><strong>Up to 5</strong> Read Replicas</li><li><strong>Within AZ,</strong> <strong>Cross AZ</strong> or <strong>Cross Region</strong></li><li>Replication is ASYNC, so <strong>reads are eventually consistent</strong></li><li>Replicas <strong>can be promoted to their own DB</strong></li><li><strong>Applications must update the connection string</strong> to leverage read replicas</li><li>For RDS Read Replicas within the <strong>same region</strong>, <strong>no network cost when data goes from one AZ to another</strong></li></ul></details><h2 id="deployment-options" tabindex="-1">Deployment Options <a class="header-anchor" href="#deployment-options" aria-label="Permalink to “Deployment Options”">​</a></h2><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><strong>Single-AZ</strong></td><td>One database instance in one availability zone (AZ).</td></tr><tr><td><strong>Multi-AZ</strong></td><td>Synchronous replication to standby in another AZ for HA and failover.</td></tr><tr><td><strong>Read Replicas</strong></td><td>Asynchronous replicas in same/different regions to offload reads.</td></tr></tbody></table>',12)]))}const m=e(r,[["render",i]]);export{h as __pageData,m as default};
